import React, { Component } from "react";

class test1 extends Component {
  state = {
    count: 0,
    args: [],
  };

  conditionsRnd() {}
  renderArgs() {
    return this.state.args.map((arg) => <li>{arg}</li>);
  }
  render() {
    return (
      <React.Fragment>
        <p>{this.state.args.length === 0 && "no args to show"}</p>
        <ul>{this.renderArgs()}</ul>
        <button className="btn badge-danger m2">render test </button>
      </React.Fragment>
    );
  }
}

export default test1;
