import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0,
  };
  render() {
    return (
      <React.Fragment>
        <h1>{this.formatCount()}</h1>
        <button>Increment</button>
      </React.Fragment>
    );
  }
  formatCount() {
    return this.state.count === 0 ? <h1>ZEro</h1> : this.state.count;
  }
}
export default Counter;
