import React, { Component } from "react";

class Count extends Component {
  state = {
    count: 0,
    args: ["arg1", "arg2", "arg3", "arg"],
  };
  dynRend() {
    {
      let csx = "badge m-4 badge-";
      csx += this.state.count === 0 ? "danger" : "success";
      return csx;
    }
  }
  sIncrement() {
    this.setState(this.state.count + 1);
  }
  render() {
    return (
      <React.Fragment>
        <span className={this.dynRend()}>{this.sIncrement()}</span>
        <button className="btn btn-warning">incriment</button>
      </React.Fragment>
    );
  }
}

export default Count;
