import React, { Component } from "react";

class test1 extends Component {
  state = {
    count: 0,
    args: ["adad", "asdad", "asdasd"],
  };
  handleAffz = () => {
    return this.setState({ count: this.state.count + 1 });
  };
  buttomRnd() {
    let butooo = "btn m-2 badge-";
    butooo += this.state.count === 0 ? "danger" : "success";
    return butooo;
  }
  renderArgs = () => {
    return this.state.args.map((arg) => (
      <li className="list-group-item">{arg}</li>
    ));
  };

  makeArgziro = () => {
    return this.setState({ args: this.state.args.splice() });
  };
  render() {
    return (
      <React.Fragment>
        <p>{this.state.args.length === 0 && "no args to show"}</p>
        <ul>{this.renderArgs()}</ul>
        <button onClick={this.handleAffz} className={this.buttomRnd()}>
          increment
        </button>
        <button className="btn m-2 badge-warning">{this.state.count}</button>
        <button onClick={this.makeArgziro} className="btn m-2 badge-info">
          delete item of array
        </button>
      </React.Fragment>
    );
  }
}

export default test1;
