import React, { Component } from "react";
class Count extends Component {
  state = {
    count: 2,
  };
  dynRend() {
    {
      let ches = "badge m-4 badge-";
      ches += this.state.count === 0 ? "danger" : "success";
      return ches;
    }
  }
  render() {
    return (
      <React.Fragment>
        <span className={this.dynRend()}>{this.state.count}</span>
        <button className="btn btn-warning">incriment</button>
      </React.Fragment>
    );
  }
}

export default Count;
