import React, { Component } from "react";

class Count extends Component {
  state = {
    count: 0,
    args: ["arg1", "arg2", "arg3", "arg"],
  };
  dynRend() {
    {
      let classes = "badge m-4 badge-";
      classes += this.state.count === 0 ? "danger" : "success";
    }
  }
  render() {
    return (
      <React.Fragment>
        <span className="badge badge-success m-4">{this.state.count}</span>
        <button className="btn btn-warning">incriment</button>
      </React.Fragment>
    );
  }
}

export default Count;
