import React, { Component } from "react";
class Count extends Component {
  state = {
    count: 2,
    args: ["arg1", "arg2", "arg3", "arg4"],
  };
  renderTag() {
    let csx = this.state.args;
    csx.length === 0
      ? "nothing to show"
      : csx.map((tag) => <li key={tag}>{tag}</li>);
  }
  render() {
    return (
      <React.Fragment>
        <ul>{this.renderTag()}</ul>
        <button className="btn btn-warning">incriment</button>
      </React.Fragment>
    );
  }
}

export default Count;
